package HashMap;import java.util.Scanner;import java.util.TreeMap;public class ques2 {    public static void main(String[] args) {        TreeMap<String,Integer> hm = new TreeMap<>();        Scanner sc = new Scanner(System.in);        System.out.println("Enter the number of entries :");        int n = sc.nextInt();        for(int i = 0; i<n; i++)        {            System.out.println("Enter the key : ");            int k = sc.nextInt();            System.out.println("Enter the value : ");            String v = sc.next();            hm.put(v,k);        }        System.out.println(hm);    }}