package Stack;import java.util.Stack;public class ques2 {    public static String decodeString(String s) {        Stack<Integer> countStack = new Stack<>();        Stack<StringBuilder> stringStack = new Stack<>();        StringBuilder currentString = new StringBuilder();        int currentCount = 0;        for (char ch : s.toCharArray()) {            if (Character.isDigit(ch)) {                currentCount = currentCount * 10 + (ch - '0');            } else if (ch == '[') {                countStack.push(currentCount);                stringStack.push(currentString);                currentCount = 0;                currentString = new StringBuilder();            } else if (ch == ']') {                int count = countStack.pop();                StringBuilder decodedString = new StringBuilder();                for (int i = 0; i < count; i++) {                    decodedString.append(currentString);                }                currentString = stringStack.pop().append(decodedString);            } else {                currentString.append(ch);            }        }        return currentString.toString();    }    public static void main(String[] args) {        String encodedString = "3[a]2[bc]";        String decodedString = decodeString(encodedString);        System.out.println("Decoded string: " + decodedString);    }}