package Stack;import java.util.Stack;public class ques3 {    public static int calPoints(String[] operations) {        Stack<Integer> stack = new Stack<>();        for (String op : operations) {            if (op.equals("C")) {                stack.pop();            } else if (op.equals("D")) {                stack.push(2 * stack.peek());            } else if (op.equals("+")) {                int prev = stack.pop();                int newScore = prev + stack.peek();                stack.push(prev);                stack.push(newScore);            } else {                stack.push(Integer.parseInt(op));            }        }        int sum = 0;        while (!stack.isEmpty()) {            sum += stack.pop();        }        return sum;    }    public static void main(String[] args) {        String[] operations = {"5", "2", "C", "D", "+"};        int totalSum = calPoints(operations);        System.out.println("Total sum of scores: " + totalSum);    }}